mapper.select:
SELECT uuid
FROM user_information
WHERE username = #{username}

SELECT uuid
FROM article
WHERE article_id = #{article_id}

SELECT username
FROM user_information
WHERE uuid = #{uuid}

mapper.update:
UPDATE article
SET status = 0
WHERE article_id = #{article_id} AND uuid = #{uuid}

UPDATE article
SET status = 2
WHERE article_id = #{article_id} AND uuid = #{uuid}

UPDATE article
SET status = #{status}
WHERE article_id = #{article_id} AND uuid = #{uuid}

UPDATE article
SET password = #{password}
WHERE article_id = #{article_id}
